import {useState} from 'react'
import './AmazingNumberButton.css'
import BeautifullScreen from '../BeautifullScreen/BeautifullScreen'
function AmazingNumberButton() {

  const [num, setNum] = useState(0);

  const ops = ['+','-','*','/','.']

  const voirNum = (i) => {
    log(i);
  }


  const ajouterBoutons = () => {
    const chiffres = []
    for (let i = 9; i > -1; i--) {
      // Méthode JS pour envoyer le résultat de la boucle for dans chiffres
      // key pour éviter d'avoir des conflits du aux id
      chiffres.push(
        <div key={i} className="buttonNumb">
          <button onClick={voirNum} key={i}>
            {i}
          </button>
        </div>
      );
    }
    return chiffres;
  }

    return (
      <div className="ok">
        <BeautifullScreen numb={voirNum} />
      <div className="AmazingNumberButton">
        {ajouterBoutons()}
      </div>
      </div>
    );
}
export default AmazingNumberButton;
